        <Container maxWidth="lg">
          <Typography
            variant="h3"
            className="text-center mb-8 font-bold"
            sx={{ color: '#000000', fontWeight: 'bold', mb: 6 }}
          >
            Imagini de la evenimente
          </Typography>

          <ImageList
            variant="masonry"
            cols={4}
            gap={16}
            sx={{
              '& .MuiImageListItem-root': {
                cursor: 'pointer',
                transition: 'transform 0.3s ease',
                '&:hover': {
                  transform: 'scale(1.05)',
                }
              }
            }}
          >
            {images.map((image) => (
              <ImageListItem
                key={image.id}
                onClick={() => handleImageClick(image)}
              >
                <img
                  src={image.src}
                  alt={image.alt}
                  loading="lazy"
                  style={{
                    borderRadius: '8px',
                    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
                  }}
                />
              </ImageListItem>
            ))}
          </ImageList>
        </Container>
      </Box>

      {/* Modal for enlarged image */}
      <Modal
        open={modalOpen}
        onClose={handleCloseModal}
        sx={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}
