import React, { useState, useEffect, useRef } from 'react';
import {
  Container,
  Typography,
  Grid,
  Box,
  Fade
} from '@mui/material';
import {
  FavoriteOutlined,
  CelebrationOutlined,
  ChildCareOutlined,
  BusinessOutlined,
  PlayArrow,
  Pause,
  SkipPrevious,
  SkipNext,
  FastRewind,
  FastForward,
  VolumeUp,
  VolumeDown
} from '@mui/icons-material';
import AudioPlayer from 'react-h5-audio-player';
import 'react-h5-audio-player/lib/styles.css';

const CounterAnimation = ({ target, prefix = '', duration = 2000 }) => {
  const [count, setCount] = useState(0);
  const [isVisible, setIsVisible] = useState(false);
  const elementRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !isVisible) {
          setIsVisible(true);
        }
      },
      { threshold: 0.5 }
    );

    if (elementRef.current) {
      observer.observe(elementRef.current);
    }

    return () => observer.disconnect();
  }, [isVisible]);

  useEffect(() => {
    if (!isVisible) return;

    const startTime = Date.now();
    const endTime = startTime + duration;

    const updateCount = () => {
      const now = Date.now();
      const progress = Math.min((now - startTime) / duration, 1);

      const easeOutCubic = 1 - Math.pow(1 - progress, 3);
      const currentCount = Math.floor(easeOutCubic * target);

      setCount(currentCount);

      if (now < endTime) {
        requestAnimationFrame(updateCount);
      } else {
        setCount(target);
      }
    };

    requestAnimationFrame(updateCount);
  }, [isVisible, target, duration]);

  return (
    <span ref={elementRef}>
      {prefix}{count}
    </span>
  );
};

const formatTime = (time) => {
  const minutes = Math.floor(time / 60);
  const seconds = Math.floor(time % 60);
  return `${minutes}:${seconds.toString().padStart(2, '0')}`;
};

const Mixes = () => {
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);

  const mixData = [
    {
      id: 1,
      src: encodeURI('/audio/[BraveDown.Com] [Dj Suru - Forever Drive Mix] [1759501976] .mp3'),
      name: 'Forever Drive Mix',
      artist: 'Dj Suru'
    },
    {
      id: 2,
      src: encodeURI('/audio/[BraveDown.Com] [BURN RESIDENCY 2017 – DjSuru] [1759502542] .mp3'),
      name: 'BURN RESIDENCY 2017',
      artist: 'Dj Suru'
    },
    {
      id: 3,
      src: encodeURI('/audio/[BraveDown.Com] [Dj Suru - Romania - MazdaSounds] [1759502701] .mp3'),
      name: 'Romania - MazdaSounds',
      artist: 'Dj Suru'
    },
    {
      id: 4,
      src: encodeURI('/audio/[BraveDown.Com] [Dj Suru - Merry Christmas] [1759502930] .mp3'),
      name: 'Merry Christmas',
      artist: 'Dj Suru'
    }
  ];

  const handleClickNext = () => {
    setCurrentTrackIndex((prev) =>
      prev < mixData.length - 1 ? prev + 1 : 0
    );
  };

  const handleClickPrevious = () => {
    setCurrentTrackIndex((prev) =>
      prev > 0 ? prev - 1 : mixData.length - 1
    );
  };


  return (
    <>
      {/* Hero Section */}
      <Box
        sx={{
          height: '60vh',
          backgroundImage: 'url(/images/banner.jpg)',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          position: 'relative',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
          }
        }}
      >
        <Box sx={{ zIndex: 2, textAlign: 'center', color: 'white' }}>
          <Typography variant="h2" sx={{ fontWeight: 'bold', mb: 2 }}>
            Mixuri
          </Typography>
          <Typography variant="h5">
            Ascultă câteva dintre mixurile mele
          </Typography>
        </Box>
      </Box>


      {/* Audio Player Section */}
      <Box sx={{ py: 10, backgroundColor: '#ffffff' }}>
        <Container maxWidth="lg">
          <Typography
            variant="h3"
            sx={{
              textAlign: 'center',
              mb: 6,
              fontWeight: 'bold',
              color: '#000000'
            }}
          >
            Demo Mixuri
          </Typography>

          <Box sx={{ mb: 4, p: 4, backgroundColor: '#f8f9fa', borderRadius: 3 }}>
            <Audio playlist={mixData}>
              {(props) => {
                if (!props) return <div>Loading...</div>;

                const {
                  playing = false,
                  togglePlay = () => {},
                  nextTrack = () => {},
                  prevTrack = () => {},
                  trackIndex = 0,
                  playlist = mixData,
                  currentTime = 0,
                  duration = 0,
                  volume = 50,
                  setVolume = () => {},
                  setCurrentTime = () => {},
                  rewindTrack = () => {},
                  forwardTrack = () => {},
                  loading = false
                } = props;

                const currentTrack = playlist[trackIndex] || mixData[0];

                return (
                  <Box>
                    <Box sx={{ display: 'flex', flexDirection: { xs: 'column', md: 'row' }, justifyContent: 'space-between', gap: 4, mb: 3 }}>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                        <Box
                          sx={{
                            width: 60,
                            height: 60,
                            backgroundColor: '#c4c4c4',
                            borderRadius: 1,
                            backgroundImage: 'url(/images/banner.jpg)',
                            backgroundSize: 'cover',
                            backgroundPosition: 'center'
                          }}
                        />
                        <Box>
                          <Typography variant="h6" sx={{ fontWeight: 'bold', textTransform: 'capitalize' }}>
                            {currentTrack?.name ? currentTrack.name.split('-').join(' ') : 'Loading...'}
                          </Typography>
                          <Typography variant="body2" sx={{ color: '#666' }}>
                            {formatTime(currentTime)} / {formatTime(duration)}
                          </Typography>
                        </Box>
                      </Box>

                      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: { xs: 'center', md: 'flex-end' }, gap: 2 }}>
                        <Box
                          component="button"
                          onClick={() => rewindTrack && rewindTrack(10)}
                          sx={{
                            border: 'none',
                            background: 'none',
                            cursor: 'pointer',
                            color: '#333',
                            '&:hover': { color: '#000' }
                          }}
                        >
                          <FastRewind />
                        </Box>

                        <Box
                          component="button"
                          onClick={prevTrack}
                          sx={{
                            border: 'none',
                            background: 'none',
                            cursor: 'pointer',
                            color: '#333',
                            '&:hover': { color: '#000' }
                          }}
                        >
                          <SkipPrevious />
                        </Box>

                        <Box
                          component="button"
                          onClick={togglePlay}
                          sx={{
                            border: 'none',
                            background: '#000',
                            color: 'white',
                            borderRadius: '50%',
                            width: 50,
                            height: 50,
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            cursor: 'pointer',
                            '&:hover': { backgroundColor: '#333' }
                          }}
                        >
                          {playing ? <Pause /> : <PlayArrow />}
                        </Box>

                        <Box
                          component="button"
                          onClick={nextTrack}
                          sx={{
                            border: 'none',
                            background: 'none',
                            cursor: 'pointer',
                            color: '#333',
                            '&:hover': { color: '#000' }
                          }}
                        >
                          <SkipNext />
                        </Box>

                        <Box
                          component="button"
                          onClick={() => forwardTrack && forwardTrack(10)}
                          sx={{
                            border: 'none',
                            background: 'none',
                            cursor: 'pointer',
                            color: '#333',
                            '&:hover': { color: '#000' }
                          }}
                        >
                          <FastForward />
                        </Box>
                      </Box>
                    </Box>

                    <Box sx={{ mb: 3 }}>
                      <Box
                        component="input"
                        type="range"
                        min="0"
                        max={duration || 0}
                        step="1"
                        value={currentTime || 0}
                        onChange={(e) => setCurrentTime && setCurrentTime(+e.target.value)}
                        sx={{
                          width: '100%',
                          height: 8,
                          borderRadius: 5,
                          background: '#ddd',
                          outline: 'none',
                          cursor: 'pointer',
                          '&::-webkit-slider-thumb': {
                            appearance: 'none',
                            width: 20,
                            height: 20,
                            borderRadius: '50%',
                            background: '#000',
                            cursor: 'pointer'
                          }
                        }}
                      />
                    </Box>

                    <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', gap: 4 }}>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                        <VolumeDown />
                        <Box
                          component="input"
                          type="range"
                          min="0"
                          max="100"
                          value={volume || 50}
                          onChange={(e) => setVolume && setVolume(+e.target.value)}
                          sx={{
                            width: { xs: '100px', md: '150px' },
                            height: 4,
                            borderRadius: 5,
                            background: '#ddd',
                            outline: 'none',
                            cursor: 'pointer',
                            '&::-webkit-slider-thumb': {
                              appearance: 'none',
                              width: 16,
                              height: 16,
                              borderRadius: '50%',
                              background: '#000',
                              cursor: 'pointer'
                            }
                          }}
                        />
                        <VolumeUp />
                      </Box>

                      {loading && (
                        <Typography variant="body2" sx={{ color: '#666' }}>
                          Loading...
                        </Typography>
                      )}
                    </Box>
                  </Box>
                );
              }}
            </Audio>
          </Box>

          <Typography
            variant="body1"
            sx={{
              textAlign: 'center',
              color: '#666666',
              fontSize: '1.1rem',
              lineHeight: 1.6
            }}
          >
            Acestea sunt doar câteva exemple din repertoriul meu. Pentru fiecare eveniment,
            creez un playlist personalizat în funcție de preferințele dumneavoastră și atmosfera dorită.
          </Typography>
        </Container>
      </Box>
    </>
  );
};

export default Mixes;
