import React, { useState, useEffect, useRef } from 'react';
import {
  Container,
  Typography,
  Grid,
  Box,
  Fade
} from '@mui/material';
import {
  FavoriteOutlined,
  CelebrationOutlined,
  ChildCareOutlined,
  BusinessOutlined
} from '@mui/icons-material';
import AudioPlayer from 'react-modern-audio-player';

const CounterAnimation = ({ target, prefix = '', duration = 2000 }) => {
  const [count, setCount] = useState(0);
  const [isVisible, setIsVisible] = useState(false);
  const elementRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !isVisible) {
          setIsVisible(true);
        }
      },
      { threshold: 0.5 }
    );

    if (elementRef.current) {
      observer.observe(elementRef.current);
    }

    return () => observer.disconnect();
  }, [isVisible]);

  useEffect(() => {
    if (!isVisible) return;

    const startTime = Date.now();
    const endTime = startTime + duration;

    const updateCount = () => {
      const now = Date.now();
      const progress = Math.min((now - startTime) / duration, 1);

      const easeOutCubic = 1 - Math.pow(1 - progress, 3);
      const currentCount = Math.floor(easeOutCubic * target);

      setCount(currentCount);

      if (now < endTime) {
        requestAnimationFrame(updateCount);
      } else {
        setCount(target);
      }
    };

    requestAnimationFrame(updateCount);
  }, [isVisible, target, duration]);

  return (
    <span ref={elementRef}>
      {prefix}{count}
    </span>
  );
};

const Mixes = () => {
  const mixData = [
    {
      name: 'Forever Drive Mix',
      writer: 'Dj Suru',
      img: '/images/banner.jpg',
      src: '/audio/[BraveDown.Com] [Dj Suru - Forever Drive Mix] [1759501976] .mp3',
      id: 1
    },
    {
      name: 'BURN RESIDENCY 2017',
      writer: 'Dj Suru',
      img: '/images/banner.jpg',
      src: '/audio/[BraveDown.Com] [BURN RESIDENCY 2017 – DjSuru] [1759502542] .mp3',
      id: 2
    },
    {
      name: 'Romania - MazdaSounds',
      writer: 'Dj Suru',
      img: '/images/banner.jpg',
      src: '/audio/[BraveDown.Com] [Dj Suru - Romania - MazdaSounds] [1759502701] .mp3',
      id: 3
    },
    {
      name: 'Merry Christmas',
      writer: 'Dj Suru',
      img: '/images/banner.jpg',
      src: '/audio/[BraveDown.Com] [Dj Suru - Merry Christmas] [1759502930] .mp3',
      id: 4
    }
  ];


  return (
    <>
      {/* Hero Section */}
      <Box
        sx={{
          height: '60vh',
          backgroundImage: 'url(/images/banner.jpg)',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          position: 'relative',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
          }
        }}
      >
        <Box sx={{ zIndex: 2, textAlign: 'center', color: 'white' }}>
          <Typography variant="h2" sx={{ fontWeight: 'bold', mb: 2 }}>
            Mixuri
          </Typography>
          <Typography variant="h5">
            Ascultă câteva dintre mixurile mele
          </Typography>
        </Box>
      </Box>

      {/* Statistics Section */}
      <Box className="py-20 bg-gray-50">
        <Container maxWidth="lg">
          <Grid container spacing={6} justifyContent="center">
            {[
              {
                number: 875,
                label: 'CLIENȚI FERICIȚI',
                icon: <FavoriteOutlined sx={{ fontSize: '8rem' }} />
              },
              {
                number: 346,
                label: 'NUNTI',
                icon: <CelebrationOutlined sx={{ fontSize: '8rem' }} />
              },
              {
                number: 209,
                label: 'BOTEZURI',
                icon: <ChildCareOutlined sx={{ fontSize: '8rem' }} />
              },
              {
                number: 125,
                label: 'EVENIMENTE CORPORATE',
                icon: <BusinessOutlined sx={{ fontSize: '8rem' }} />
              }
            ].map((stat, index) => (
              <Grid item xs={12} sm={6} md={3} key={index}>
                <Fade in timeout={1000 + index * 200}>
                  <Box
                    className="text-center"
                    sx={{
                      position: 'relative',
                      py: 4,
                      px: 2,
                      '&:not(:last-child)::after': {
                        content: '""',
                        position: 'absolute',
                        right: { xs: 'auto', md: '-3rem' },
                        bottom: { xs: '-3rem', md: 'auto' },
                        left: { xs: '50%', md: 'auto' },
                        top: { xs: 'auto', md: '50%' },
                        transform: { xs: 'translateX(-50%)', md: 'translateY(-50%)' },
                        width: { xs: '80px', md: '2px' },
                        height: { xs: '2px', md: '80px' },
                        backgroundColor: '#cccccc'
                      }
                    }}
                  >
                    {/* Background Icon */}
                    <Box
                      sx={{
                        position: 'absolute',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                        color: '#000000',
                        opacity: 0.1,
                        zIndex: 1
                      }}
                    >
                      {stat.icon}
                    </Box>
                    <Typography
                      variant="h2"
                      sx={{
                        fontSize: { xs: '2.5rem', md: '3rem' },
                        fontWeight: 'bold',
                        color: '#000000',
                        mb: 1,
                        position: 'relative',
                        zIndex: 2
                      }}
                    >
                      <CounterAnimation
                        target={stat.number}
                        prefix={stat.prefix || ''}
                        duration={6000}
                      />
                    </Typography>
                    <Typography
                      variant="h6"
                      sx={{
                        color: '#666666',
                        fontWeight: 'bold',
                        letterSpacing: '1px',
                        position: 'relative',
                        zIndex: 2
                      }}
                    >
                      {stat.label}
                    </Typography>
                  </Box>
                </Fade>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* Audio Player Section */}
      <Box sx={{ py: 10, backgroundColor: '#ffffff' }}>
        <Container maxWidth="lg">
          <Typography
            variant="h3"
            sx={{
              textAlign: 'center',
              mb: 6,
              fontWeight: 'bold',
              color: '#000000'
            }}
          >
            Demo Mixuri
          </Typography>

          <Box sx={{ mb: 4 }}>
            <AudioPlayer
              playList={mixData}
              audioInitialState={{
                isPlaying: false,
                volume: 0.5
              }}
              placement={{
                interface: {
                  templateArea: {
                    trackTimeCurrent: 'row1-5',
                    trackTimeDuration: 'row1-6',
                    progress: 'row1-7',
                    playButton: 'row2-1',
                    repeatType: 'row2-2',
                    volume: 'row2-3',
                    playList: 'row2-4'
                  }
                }
              }}
              activeUI={{
                all: true,
                playButton: true,
                prevNnext: true,
                volume: true,
                volumeSlider: true,
                repeatType: true,
                trackTime: true,
                trackInfo: true,
                playList: true,
                progress: 'waveform'
              }}
            />
          </Box>

          <Typography
            variant="body1"
            sx={{
              textAlign: 'center',
              color: '#666666',
              fontSize: '1.1rem',
              lineHeight: 1.6
            }}
          >
            Acestea sunt doar câteva exemple din repertoriul meu. Pentru fiecare eveniment,
            creez un playlist personalizat în funcție de preferințele dumneavoastră și atmosfera dorită.
          </Typography>
        </Container>
      </Box>
    </>
  );
};

export default Mixes;
