import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Button,
  Grid,
  Card,
  CardContent,
  Box,
  Chip,
  Fade
} from '@mui/material';
import GlassIcons from '../components/GlassIcons';
import {
  MusicNote,
  Event,
  People,
  Star,
  FavoriteOutlined,
  CelebrationOutlined,
  ChildCareOutlined,
  BusinessOutlined
} from '@mui/icons-material';
import ReviewsCarousel from '../components/ReviewsCarousel';
import CardSwap, { Card as SwapCard } from '../components/CardSwap';
import './Home.css';

const CounterAnimation = ({ target, prefix = '', duration = 2000 }) => {
  const [count, setCount] = useState(0);
  const [isVisible, setIsVisible] = useState(false);
  const elementRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !isVisible) {
          setIsVisible(true);
        }
      },
      { threshold: 0.5 }
    );

    if (elementRef.current) {
      observer.observe(elementRef.current);
    }

    return () => observer.disconnect();
  }, [isVisible]);

  useEffect(() => {
    if (!isVisible) return;

    const startTime = Date.now();
    const endTime = startTime + duration;

    const updateCount = () => {
      const now = Date.now();
      const progress = Math.min((now - startTime) / duration, 1);

      const easeOutCubic = 1 - Math.pow(1 - progress, 3);
      const currentCount = Math.floor(easeOutCubic * target);

      setCount(currentCount);

      if (now < endTime) {
        requestAnimationFrame(updateCount);
      } else {
        setCount(target);
      }
    };

    requestAnimationFrame(updateCount);
  }, [isVisible, target, duration]);

  return (
    <span ref={elementRef}>
      {prefix}{count}
    </span>
  );
};

const Home = () => {
  const navigate = useNavigate();

  const services = [
    {
      id: 'nunta',
      title: 'Nuntă',
      image: '/images/servicii/nunta.jpg',
      ambientColor: '#E8B4CB',
      description: 'Creez momente magice pentru ziua voastră specială',
      features: [
        'Muzică pentru ceremonia religioasă',
        'Cocktail și cină',
        'Deschiderea balului',
        'Petrecerea de nuntă',
        'Echipament audio profesional',
        'Microfoane pentru oficiant și mireasă'
      ]
    },
    {
      id: 'clubbing',
      title: 'Clubbing',
      image: '/images/servicii/clubbing.png',
      ambientColor: '#4A90E2',
      description: 'Energia pură a muzicii electronice',
      features: [
        'Set-uri EDM și House',
        'Lighting show sincronizat',
        'Mixuri live exclusiviste',
        'Atmosferă de club autentic',
        'Interacțiune cu publicul'
      ]
    },
    {
      id: 'botez',
      title: 'Botez',
      image: '/images/servicii/botez.jpg',
      ambientColor: '#F5E6A8',
      description: 'Sărbătorind binecuvântarea celui mic',
      features: [
        'Muzică ambientală pentru ceremonie',
        'Animație pentru copii',
        'Muzică pentru masa festivă',
        'Jocuri și concursuri',
        'Sonorizare discursuri'
      ]
    },
    {
      id: 'majorat',
      title: 'Majorat',
      image: '/images/servicii/majorat.jpg',
      ambientColor: '#B19CD9',
      description: 'Petrecere de neuitat pentru vârsta majoratului',
      features: [
        'Muzică actuală pentru tineri',
        'Animație și jocuri',
        'Efecte de lumină',
        'Karaoke',
        'Dedicații muzicale'
      ]
    },
    {
      id: 'corporate',
      title: 'Corporate',
