import React, { useState, useEffect } from 'react';
import { Box, Typography, Container } from '@mui/material';
import { Star } from '@mui/icons-material';

const ReviewsCarousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [touchStartX, setTouchStartX] = useState(0);
  const [touchEndX, setTouchEndX] = useState(0);

  const reviews = [
    {
      name: "Maria & Alexandru",
      event: "Nuntă",
      text: "DJ Suru a fost absolut fantastic la nunta noastră! Muzica a fost perfectă și atmosfera incredibilă. Toți invitații au dansat toată noaptea!"
    },
    {
      name: "Ana Popescu",
      event: "Botez",
      text: "Profesionist desăvârșit! A știut exact ce muzică să pună pentru copii și pentru adulți. Botezul a fost memorabil datorită lui."
    },
    {
      name: "Mihai Ionescu",
      event: "Majorat",
      text: "Cel mai tare DJ! A creat exact atmosfera pe care o doream pentru majoratul fiului meu. Echipamentul de top și muzica perfectă!"
    },
    {
      name: "Elena & Cristian",
      event: "Eveniment Privat",
      text: "Am organizat o petrecere de aniversare și DJ Suru a depășit toate așteptările. Profesional, punctual și cu muzică de excepție!"
    },
    {
      name: "Familia Radu",
      event: "Nuntă",
      text: "Recomand cu încredere! DJ Suru a înțeles perfect gusturile noastre muzicale și a adaptat playlist-ul perfect pentru oaspeți."
    }
  ];

  const totalCards = reviews.length;

  const changeSlide = (direction) => {
    let newSlide = currentSlide + direction;

    if (newSlide < 0) {
      newSlide = totalCards - 1;
    } else if (newSlide >= totalCards) {
      newSlide = 0;
    }

    setCurrentSlide(newSlide);
  };

  const goToSlide = (slideIndex) => {
    setCurrentSlide(slideIndex);
  };

  const handleTouchStart = (e) => {
    setTouchStartX(e.changedTouches[0].screenX);
  };

  const handleTouchEnd = (e) => {
    setTouchEndX(e.changedTouches[0].screenX);
    handleSwipe();
  };

  const handleSwipe = () => {
    const swipeThreshold = 50;
    const diff = touchStartX - touchEndX;

    if (Math.abs(diff) > swipeThreshold) {
      if (diff > 0) {
        changeSlide(1);
      } else {
        changeSlide(-1);
      }
    }
  };

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'ArrowLeft') {
        changeSlide(-1);
      } else if (e.key === 'ArrowRight') {
        changeSlide(1);
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [currentSlide]);

  const getCardClass = (index) => {
    if (index === currentSlide) return 'tt-active';
    if (index === currentSlide - 1 || (currentSlide === 0 && index === totalCards - 1)) return 'tt-prev';
    if (index === currentSlide + 1 || (currentSlide === totalCards - 1 && index === 0)) return 'tt-next';
    return 'tt-hidden';
  };

  return (
    <Box
      className="tt-agency-services-section"
      sx={{
        padding: '80px 0',
        backgroundColor: '#ffffff',
        '& .tt-agency-services-header': {
          textAlign: 'center',
          marginBottom: '60px'
        },
        '& .tt-agency-services-title': {
          fontSize: { xs: '2.5rem', md: '3.5rem' },
          fontWeight: 'bold',
          color: '#000000',
          marginBottom: '16px',
          lineHeight: '1.2'
        },
        '& .tt-agency-services-subtitle': {
          fontSize: '1.2rem',
          color: '#666666',
          fontWeight: 300
        },
        '& .tt-agency-carousel-container': {
          position: 'relative',
          maxWidth: '1000px',
          margin: '0 auto',
          height: '500px'
        },
        '& .tt-agency-carousel-wrapper': {
          position: 'relative',
          width: '100%',
          height: '400px',
          overflow: 'hidden',
          perspective: '1000px'
        },
        '& .tt-agency-service-card': {
          position: 'absolute',
          left: '50%',
          top: '50%',
          width: '400px',
          height: '300px',
          background: 'rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          borderRadius: '20px',
          padding: '2rem',
          textAlign: 'center',
          transition: 'all 0.8s cubic-bezier(0.4, 0, 0.2, 1)',
          transformStyle: 'preserve-3d',
          boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
          '&.tt-active': {
            transform: 'translate(-50%, -50%) scale(1) rotateY(0deg)',
            opacity: 1,
            zIndex: 3
          },
          '&.tt-prev': {
            transform: 'translate(-150%, -50%) scale(0.8) rotateY(45deg)',
            opacity: 0.6,
            zIndex: 2
          },
          '&.tt-next': {
            transform: 'translate(50%, -50%) scale(0.8) rotateY(-45deg)',
            opacity: 0.6,
            zIndex: 2
          },
          '&.tt-hidden': {
            transform: 'translate(-50%, -50%) scale(0.6) rotateY(90deg)',
            opacity: 0,
            zIndex: 1
          }
        },
        '& .tt-agency-service-header': {
          marginBottom: '1.5rem'
        },
        '& .tt-agency-service-icon': {
          fontSize: '3rem',
          marginBottom: '1rem',
          display: 'block'
        },
        '& .tt-agency-service-name': {
          fontSize: '1.5rem',
          fontWeight: 'bold',
          color: '#000000',
          marginBottom: '1rem',
          lineHeight: '1.3'
        },
        '& .tt-agency-service-description': {
          fontSize: '1rem',
          color: '#555555',
          lineHeight: '1.6'
        },
        '& .tt-agency-carousel-nav': {
          position: 'absolute',
          top: '50%',
          width: '50px',
          height: '50px',
          background: 'rgba(0, 0, 0, 0.8)',
          color: '#ffffff',
          border: 'none',
          borderRadius: '50%',
          fontSize: '1.5rem',
          cursor: 'pointer',
          zIndex: 4,
          transition: 'all 0.3s ease',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          transform: 'translateY(-50%)',
          '&:hover': {
            background: 'rgba(0, 0, 0, 1)',
            transform: 'translateY(-50%) scale(1.1)'
          },
          '&.tt-prev': {
            left: '20px'
          },
          '&.tt-next': {
            right: '20px'
          }
        },
        '& .tt-agency-carousel-indicators': {
          display: 'flex',
          justifyContent: 'center',
          gap: '10px',
          marginTop: '3rem'
        },
        '& .tt-agency-indicator': {
          width: '12px',
          height: '12px',
          borderRadius: '50%',
          backgroundColor: '#cccccc',
          cursor: 'pointer',
          transition: 'all 0.3s ease',
          '&:hover': {
            backgroundColor: '#000000',
            transform: 'scale(1.2)'
          },
          '&.tt-active': {
            backgroundColor: '#000000'
          }
        }
      }}
    >
      <Container maxWidth="lg">
        <div className="tt-agency-services-header">
          <h2 className="tt-agency-services-title">RECENZII CLIENȚI</h2>
          <p className="tt-agency-services-subtitle">Ce spun cei care au ales DJ Suru</p>
        </div>

        <div className="tt-agency-carousel-container">
          <div
            className="tt-agency-carousel-wrapper"
            onTouchStart={handleTouchStart}
            onTouchEnd={handleTouchEnd}
          >
            {reviews.map((review, index) => (
              <div
                key={index}
                className={`tt-agency-service-card ${getCardClass(index)}`}
              >
                <div className="tt-agency-service-header">
                  <h3 className="tt-agency-service-name">{review.name}</h3>
                  <div style={{ marginBottom: '1.5rem' }}>
                    {[...Array(5)].map((_, i) => (
                      <Star key={i} sx={{ color: '#FFD700', fontSize: '1.2rem', marginRight: '2px' }} />
                    ))}
                  </div>
                </div>
                <p className="tt-agency-service-description">"{review.text}"</p>
              </div>
            ))}

            <button
              className="tt-agency-carousel-nav tt-prev"
              onClick={() => changeSlide(-1)}
            >
              ‹
            </button>
            <button
              className="tt-agency-carousel-nav tt-next"
              onClick={() => changeSlide(1)}
            >
              ›
            </button>
          </div>

          <div className="tt-agency-carousel-indicators">
            {reviews.map((_, index) => (
              <div
                key={index}
                className={`tt-agency-indicator ${index === currentSlide ? 'tt-active' : ''}`}
                onClick={() => goToSlide(index)}
              />
            ))}
          </div>
        </div>
      </Container>
    </Box>
  );
};

export default ReviewsCarousel;
