import React from 'react';
import { Link } from 'react-router-dom';

const Services = () => {
  const services = [
    {
      title: 'Nunți',
      features: [
        'Muzică pentru ceremonia religioasă',
        'Cocktail și cină',
        'Deschiderea balului',
        'Petrecerea de nuntă',
        'Echipament audio profesional',
        'Microfoane pentru oficiant și mireasă'
      ]
    },
    {
      title: 'Botez',
      features: [
        'Muzică ambientală pentru ceremonie',
        'Animație pentru copii',
        'Muzică pentru masa festivă',
        'Jocuri și concursuri',
        'Sonorizare discursuri'
      ]
    },
    {
      title: 'Majorat',
      features: [
        'Muzică actuală pentru tineri',
        'Animație și jocuri',
        'Efecte de lumină',
        'Karaoke',
        'Dedicații muzicale'
      ]
    },
    {
      title: 'Evenimente Private',
      features: [
        'Aniversări',
        'Petreceri corporative',
        'Lansări de produse',
        'Evenimente de Crăciun',
        'Revelion'
      ]
    }
  ];

  const equipment = [
    {
      title: 'Audio',
      description: 'Sisteme audio profesionale de înaltă calitate pentru orice dimensiune de eveniment'
    },
    {
      title: 'Microfoane',
      description: 'Microfoane wireless pentru oficianți, discursuri și animație'
    },
    {
      title: 'Efecte de Lumină',
      description: 'Iluminat profesional pentru a crea atmosfera perfectă'
    },
    {
      title: 'Setup Complet',
      description: 'Instalare și configurare completă a echipamentului'
    }
  ];

  return (
    <>
      <section className="page-header">
        <div className="container">
          <h1>Servicii DJ</h1>
          <p>Servicii profesionale pentru toate tipurile de evenimente</p>
        </div>
      </section>

      <section className="services">
        <div className="container">
          <div className="services-grid">
            {services.map((service, index) => (
              <div key={index} className="service-card">
                <h3>{service.title}</h3>
                <ul>
                  {service.features.map((feature, featureIndex) => (
                    <li key={featureIndex}>{feature}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>

          <div className="equipment-section">
            <h2>Echipament Inclus</h2>
            <div className="equipment-grid">
              {equipment.map((item, index) => (
                <div key={index} className="equipment-item">
                  <h4>{item.title}</h4>
                  <p>{item.description}</p>
                </div>
              ))}
            </div>
          </div>

          <div className="pricing-section">
            <h2>Pachete și Prețuri</h2>
            <p>Prețurile variază în funcție de tipul evenimentului, durata și locația. Contactează-mă pentru o ofertă personalizată.</p>
            <Link to="/contact" className="cta-button">Solicită Ofertă</Link>
          </div>
        </div>
      </section>
    </>
  );
};

export default Services;
