import React from 'react';
import { Link } from 'react-router-dom';
import {
  Container,
  Typography,
  Button,
  Grid,
  Card,
  CardContent,
  Box,
  Chip,
  Fade
} from '@mui/material';
import {
  MusicNote,
  Event,
  People,
  Star
} from '@mui/icons-material';
import ReviewsCarousel from '../components/ReviewsCarousel';

const Home = () => {
  return (
    <>
      {/* Hero Section with Banner */}
      <Box
        className="relative flex items-center justify-center overflow-hidden"
        sx={{
          height: '100vh',
          minHeight: '800px',
          backgroundImage: 'url(/images/banner.jpg)',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundAttachment: 'fixed',
          position: 'relative',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            zIndex: 1,
          }
        }}
      >
        <Box
          sx={{
            zIndex: 2,
            position: 'absolute',
            bottom: '20%',
            left: '50%',
            transform: 'translateX(-50%)',
            textAlign: 'center'
          }}
        >
          <Fade in timeout={1000}>
            <Typography
              variant="h3"
              sx={{
                color: 'rgba(255, 255, 255, 0.6)',
                fontFamily: '"Arial", "Helvetica", sans-serif',
                fontWeight: 'bold',
                fontSize: { xs: '2.5rem', md: '3.5rem' },
                mb: 4,
                textShadow: '2px 2px 4px rgba(0,0,0,0.7)',
                fontStyle: 'italic',
                letterSpacing: '2px'
              }}
            >
              „Muzica e singura limbă pe care o înțeleg toți."
            </Typography>
          </Fade>
          <Fade in timeout={1500}>
            <Button
              component={Link}
              to="/contact"
              variant="contained"
              size="large"
              sx={{
                backgroundColor: '#ffffff',
                color: '#000000',
                fontWeight: 'bold',
                fontSize: '1.3rem',
                px: 6,
                py: 2,
                borderRadius: '50px',
                textTransform: 'uppercase',
                letterSpacing: '2px',
                boxShadow: '0 10px 30px rgba(0,0,0,0.4)',
                '&:hover': {
                  backgroundColor: '#f0f0f0',
                  transform: 'scale(1.08)',
                  boxShadow: '0 15px 40px rgba(0,0,0,0.5)',
                },
                transition: 'all 0.3s ease'
              }}
            >
              Contactează-mă
            </Button>
          </Fade>
        </Box>
      </Box>

      {/* Statistics Section */}
      <Box className="py-20 bg-gray-50">
        <Container maxWidth="lg">
          <Typography
            variant="h2"
            className="text-center mb-16 font-bold text-4xl md:text-5xl"
            sx={{ color: '#000000', fontWeight: 'bold' }}
          >
            Statistici
          </Typography>

          <Grid container spacing={4} justifyContent="center">
            {[
              {
                number: 875,
                label: 'CLIENȚI FERICIȚI',
                prefix: 'PESTE '
              },
              {
                number: 346,
                label: 'NUNTI'
              },
              {
                number: 209,
                label: 'BOTEZURI'
              },
              {
                number: 125,
                label: 'EVENIMENTE CORPORATE'
              }
            ].map((stat, index) => (
              <Grid item xs={6} md={3} key={index}>
                <Fade in timeout={1000 + index * 200}>
                  <Box className="text-center">
                    <Typography
                      variant="h2"
                      sx={{
                        fontSize: { xs: '2.5rem', md: '3rem' },
                        fontWeight: 'bold',
                        color: '#000000',
                        mb: 1
                      }}
                    >
                      {stat.prefix || ''}<span className="counter-number">{stat.number}</span>
                    </Typography>
                    <Typography
                      variant="h6"
                      sx={{
                        color: '#666666',
                        fontWeight: 'bold',
                        letterSpacing: '1px'
                      }}
                    >
                      {stat.label}
                    </Typography>
                  </Box>
                </Fade>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>


      {/* Reviews Carousel Section */}
      <ReviewsCarousel />
    </>
  );
};

export default Home;
