import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Services.css';

const Services = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const services = [
    {
      id: 'nunta',
      title: 'Nuntă',
      image: '/images/servicii/nunta.jpg',
      ambientColor: '#E8B4CB',
      description: 'Creez momente magice pentru ziua voastră specială',
      features: [
        'Muzică pentru ceremonia religioasă',
        'Cocktail și cină',
        'Deschiderea balului',
        'Petrecerea de nuntă',
        'Echipament audio profesional',
        'Microfoane pentru oficiant și mireasă'
      ]
    },
    {
      id: 'clubbing',
      title: 'Clubbing',
      image: '/images/servicii/clubbing.png',
      ambientColor: '#4A90E2',
      description: 'Energia pură a muzicii electronice',
      features: [
        'Set-uri EDM și House',
        'Lighting show sincronizat',
        'Mixuri live exclusiviste',
        'Atmosferă de club autentic',
        'Interacțiune cu publicul'
      ]
    },
    {
      id: 'botez',
      title: 'Botez',
      image: '/images/servicii/botez.jpg',
      ambientColor: '#F5E6A8',
      description: 'Sărbătorind binecuvântarea celui mic',
      features: [
        'Muzică ambientală pentru ceremonie',
        'Animație pentru copii',
        'Muzică pentru masa festivă',
        'Jocuri și concursuri',
        'Sonorizare discursuri'
      ]
    },
    {
      id: 'majorat',
      title: 'Majorat',
      image: '/images/servicii/majorat.jpg',
      ambientColor: '#B19CD9',
      description: 'Petrecere de neuitat pentru vârsta majoratului',
      features: [
        'Muzică actuală pentru tineri',
        'Animație și jocuri',
        'Efecte de lumină',
        'Karaoke',
        'Dedicații muzicale'
      ]
    },
    {
      id: 'corporate',
      title: 'Corporate',
      image: '/images/servicii/corporate.jpg',
      ambientColor: '#95C8A8',
      description: 'Profesionalism pentru evenimentele voastre de business',
      features: [
        'Lansări de produse',
        'Petreceri corporative',
        'Evenimente de Crăciun',
        'Revelion corporate',
        'Team building muzical'
      ]
    },
    {
      id: 'bal',
      title: 'Bal',
      image: '/images/servicii/bal.jpg',
      ambientColor: '#FFB347',
      description: 'Eleganță și rafinament pentru balurile dvs.',
      features: [
        'Muzică clasică și wals',
        'Muzică modernă pentru tineri',
        'Deschiderea balului',
        'Animație elegantă',
        'Sonorizare perfectă',
        'Atmosferă sofisticată'
      ]
    }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % services.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [services.length]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % services.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + services.length) % services.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const currentService = services[currentSlide];

  return (
    <>
      <section className="services-carousel-section">
        <div className="carousel-container">
          <div
            className="carousel-track"
            style={{ transform: `translateX(-${currentSlide * 100}%)` }}
          >
            {services.map((service) => (
              <div key={service.id} className="carousel-slide">
                <div className="banner-container">
                  <img
                    src={service.image}
                    alt={service.title}
                    className="service-banner"
                  />
                  <div className="banner-overlay">
                    <div className="banner-content">
                      <h1 className="service-title">{service.title}</h1>
                      <p className="service-description">{service.description}</p>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <button className="carousel-btn prev" onClick={prevSlide}>
            <span>‹</span>
          </button>
          <button className="carousel-btn next" onClick={nextSlide}>
            <span>›</span>
          </button>

          <div className="carousel-indicators">
            {services.map((_, index) => (
              <button
                key={index}
                className={`indicator ${index === currentSlide ? 'active' : ''}`}
                onClick={() => goToSlide(index)}
              />
            ))}
          </div>
        </div>
      </section>

      <section className="service-details">
        <div className="container">
          <div className="service-features">
            <h2>Ce include serviciul {currentService.title}</h2>
            <div className="features-grid">
              {currentService.features.map((feature, index) => (
                <div key={index} className="feature-item">
                  <span className="feature-icon">✓</span>
                  <span className="feature-text">{feature}</span>
                </div>
              ))}
            </div>
          </div>

          <div className="cta-section">
            <h2>Gata să începem?</h2>
            <p>Contactează-mă pentru o ofertă personalizată pentru evenimentul tău</p>
            <Link to="/contact" className="cta-button">Solicită Ofertă</Link>
          </div>
        </div>
      </section>
    </>
  );
};

export default Services;
