import React, { useRef, useEffect } from 'react';

const Mixes = () => {
  const audioRefs = useRef([]);

  const mixData = [
    {
      title: 'Mix Wedding Special',
      description: 'O selecție specială pentru nunți - muzică românească și internațională',
      audioSrc: '/audio/wedding-mix.mp3'
    },
    {
      title: 'Party Mix 2024',
      description: 'Hiturile anului 2024 într-un mix energic pentru petreceri',
      audioSrc: '/audio/party-mix-2024.mp3'
    },
    {
      title: 'Românesc Traditional',
      description: 'Muzică românească tradițională pentru evenimente speciale',
      audioSrc: '/audio/traditional-romanian.mp3'
    },
    {
      title: 'Dance Floor Classics',
      description: 'Clasicele dance care nu lipsesc de la nicio petrecere',
      audioSrc: '/audio/dance-classics.mp3'
    }
  ];

  useEffect(() => {
    const audioElements = audioRefs.current;

    const handlePlay = (playingIndex) => {
      audioElements.forEach((audio, index) => {
        if (audio && index !== playingIndex && !audio.paused) {
          audio.pause();
        }
      });
    };

    audioElements.forEach((audio, index) => {
      if (audio) {
        audio.addEventListener('play', () => handlePlay(index));
      }
    });

    return () => {
      audioElements.forEach((audio, index) => {
        if (audio) {
          audio.removeEventListener('play', () => handlePlay(index));
        }
      });
    };
  }, []);

  return (
    <>
      <section className="page-header">
        <div className="container">
          <h1>Mixuri</h1>
          <p>Ascultă câteva dintre mixurile mele</p>
        </div>
      </section>

      <section className="mixes">
        <div className="container">
          <div className="mix-list">
            {mixData.map((mix, index) => (
              <div key={index} className="mix-item">
                <div className="mix-info">
                  <h3>{mix.title}</h3>
                  <p>{mix.description}</p>
                </div>
                <div className="audio-player">
                  <audio
                    ref={el => audioRefs.current[index] = el}
                    controls
                  >
                    <source src={mix.audioSrc} type="audio/mpeg" />
                    Browserul tău nu suportă elementul audio.
                  </audio>
                </div>
              </div>
            ))}
          </div>

          <div className="mix-note">
            <h2>Demo Mixuri</h2>
            <p>Acestea sunt doar câteva exemple din repertoriul meu. Pentru fiecare eveniment, creez un playlist personalizat în funcție de preferințele dumneavoastră și atmosfera dorită.</p>
          </div>
        </div>
      </section>
    </>
  );
};

export default Mixes;
