import React, { useEffect, useState } from 'react';

const CustomCursor = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [followerPosition, setFollowerPosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    // Only run on desktop
    if (window.innerWidth <= 768) return;

    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    document.addEventListener('mousemove', handleMouseMove);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  useEffect(() => {
    const animate = () => {
      setCursorPosition(prev => ({
        x: prev.x + (mousePosition.x - prev.x) * 0.9,
        y: prev.y + (mousePosition.y - prev.y) * 0.9
      }));

      setFollowerPosition(prev => ({
        x: prev.x + (mousePosition.x - prev.x) * 0.15,
        y: prev.y + (mousePosition.y - prev.y) * 0.15
      }));

      requestAnimationFrame(animate);
    };

    const animationId = requestAnimationFrame(animate);

    return () => {
      cancelAnimationFrame(animationId);
    };
  }, [mousePosition]);

  // Only render on desktop
  if (window.innerWidth <= 768) return null;

  return (
    <>
      <div
        className="cursor"
        style={{
          position: 'fixed',
          left: cursorPosition.x - 6,
          top: cursorPosition.y - 6,
          width: '12px',
          height: '12px',
          backgroundColor: '#000000',
          borderRadius: '50%',
          pointerEvents: 'none',
          zIndex: 9999,
          opacity: 1,
          transition: 'opacity 0.3s ease'
        }}
      />
      <div
        className="cursor-follower"
        style={{
          position: 'fixed',
          left: followerPosition.x - 15,
          top: followerPosition.y - 15,
          width: '30px',
          height: '30px',
          backgroundImage: 'url(/images/disk.png)',
          backgroundSize: 'contain',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          pointerEvents: 'none',
          zIndex: 9998,
          opacity: 0.6,
          transition: 'opacity 0.3s ease'
        }}
      />
    </>
  );
};

export default CustomCursor;
