import React, { useState, useEffect, useRef } from 'react';
import {
  Container,
  Typography,
  Grid,
  Box,
  Fade
} from '@mui/material';
import {
  FavoriteOutlined,
  CelebrationOutlined,
  ChildCareOutlined,
  BusinessOutlined,
  PlayArrow,
  Pause,
  SkipPrevious,
  SkipNext,
  FastRewind,
  FastForward,
  VolumeUp,
  VolumeDown
} from '@mui/icons-material';
import AudioPlayer from 'react-h5-audio-player';
import 'react-h5-audio-player/lib/styles.css';

const CounterAnimation = ({ target, prefix = '', duration = 2000 }) => {
  const [count, setCount] = useState(0);
  const [isVisible, setIsVisible] = useState(false);
  const elementRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !isVisible) {
          setIsVisible(true);
        }
      },
      { threshold: 0.5 }
    );

    if (elementRef.current) {
      observer.observe(elementRef.current);
    }

    return () => observer.disconnect();
  }, [isVisible]);

  useEffect(() => {
    if (!isVisible) return;

    const startTime = Date.now();
    const endTime = startTime + duration;

    const updateCount = () => {
      const now = Date.now();
      const progress = Math.min((now - startTime) / duration, 1);

      const easeOutCubic = 1 - Math.pow(1 - progress, 3);
      const currentCount = Math.floor(easeOutCubic * target);

      setCount(currentCount);

      if (now < endTime) {
        requestAnimationFrame(updateCount);
      } else {
        setCount(target);
      }
    };

    requestAnimationFrame(updateCount);
  }, [isVisible, target, duration]);

  return (
    <span ref={elementRef}>
      {prefix}{count}
    </span>
  );
};

const formatTime = (time) => {
  const minutes = Math.floor(time / 60);
  const seconds = Math.floor(time % 60);
  return `${minutes}:${seconds.toString().padStart(2, '0')}`;
};

const Mixes = () => {
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);

  const mixData = [
    {
      id: 1,
      src: encodeURI('/audio/[BraveDown.Com] [Dj Suru - Forever Drive Mix] [1759501976] .mp3'),
      name: 'Forever Drive Mix',
      artist: 'Dj Suru'
    },
    {
      id: 2,
      src: encodeURI('/audio/[BraveDown.Com] [BURN RESIDENCY 2017 – DjSuru] [1759502542] .mp3'),
      name: 'BURN RESIDENCY 2017',
      artist: 'Dj Suru'
    },
    {
      id: 3,
      src: encodeURI('/audio/[BraveDown.Com] [Dj Suru - Romania - MazdaSounds] [1759502701] .mp3'),
      name: 'Romania - MazdaSounds',
      artist: 'Dj Suru'
    },
    {
      id: 4,
      src: encodeURI('/audio/[BraveDown.Com] [Dj Suru - Merry Christmas] [1759502930] .mp3'),
      name: 'Merry Christmas',
      artist: 'Dj Suru'
    }
  ];

  const handleClickNext = () => {
    setCurrentTrackIndex((prev) =>
      prev < mixData.length - 1 ? prev + 1 : 0
    );
  };

  const handleClickPrevious = () => {
    setCurrentTrackIndex((prev) =>
      prev > 0 ? prev - 1 : mixData.length - 1
    );
  };


  return (
    <>
      {/* Hero Section */}
      <Box
        sx={{
          height: '60vh',
          backgroundImage: 'url(/images/banner.jpg)',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          position: 'relative',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
          }
        }}
      >
        <Box sx={{ zIndex: 2, textAlign: 'center', color: 'white' }}>
          <Typography variant="h2" sx={{ fontWeight: 'bold', mb: 2 }}>
            Mixuri
          </Typography>
          <Typography variant="h5">
            Ascultă câteva dintre mixurile mele
          </Typography>
        </Box>
      </Box>


      {/* Audio Player Section */}
      <Box sx={{ py: 10, backgroundColor: '#ffffff' }}>
        <Container maxWidth="lg">
          <Typography
            variant="h3"
            sx={{
              textAlign: 'center',
              mb: 6,
              fontWeight: 'bold',
              color: '#000000'
            }}
          >
            Demo Mixuri
          </Typography>

          <Box sx={{ mb: 4 }}>
            {/* Current Track Info */}
            <Box sx={{ mb: 3, p: 3, backgroundColor: '#f8f9fa', borderRadius: 2 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 2 }}>
                <Box
                  sx={{
                    width: 60,
                    height: 60,
                    backgroundColor: '#c4c4c4',
                    borderRadius: 1,
                    backgroundImage: 'url(/images/banner.jpg)',
                    backgroundSize: 'cover',
                    backgroundPosition: 'center'
                  }}
                />
                <Box>
                  <Typography variant="h6" sx={{ fontWeight: 'bold' }}>
                    {mixData[currentTrackIndex].name}
                  </Typography>
                  <Typography variant="body2" sx={{ color: '#666' }}>
                    by {mixData[currentTrackIndex].artist}
                  </Typography>
                </Box>
              </Box>

              {/* Audio Player */}
              <AudioPlayer
                src={mixData[currentTrackIndex].src}
                onClickNext={handleClickNext}
                onClickPrevious={handleClickPrevious}
                showSkipControls
                showJumpControls={false}
                onEnded={handleClickNext}
                autoPlay={false}
                style={{
                  backgroundColor: 'transparent',
                  boxShadow: 'none'
                }}
              />
            </Box>

            {/* Playlist */}
            <Box>
              <Typography variant="h6" sx={{ mb: 2, fontWeight: 'bold' }}>
                Playlist
              </Typography>
              {mixData.map((track, index) => (
                <Box
                  key={track.id}
                  onClick={() => setCurrentTrackIndex(index)}
                  sx={{
                    p: 2,
                    mb: 1,
                    border: '1px solid #e0e0e0',
                    borderRadius: 1,
                    cursor: 'pointer',
                    backgroundColor: currentTrackIndex === index ? '#f0f0f0' : 'transparent',
                    '&:hover': {
                      backgroundColor: '#f5f5f5'
                    },
                    transition: 'background-color 0.2s'
                  }}
                >
                  <Typography variant="body1" sx={{ fontWeight: currentTrackIndex === index ? 'bold' : 'normal' }}>
                    {track.name}
                  </Typography>
                  <Typography variant="body2" sx={{ color: '#666' }}>
                    {track.artist}
                  </Typography>
                </Box>
              ))}
            </Box>
          </Box>

          <Typography
            variant="body1"
            sx={{
              textAlign: 'center',
              color: '#666666',
              fontSize: '1.1rem',
              lineHeight: 1.6
            }}
          >
            Acestea sunt doar câteva exemple din repertoriul meu. Pentru fiecare eveniment,
            creez un playlist personalizat în funcție de preferințele dumneavoastră și atmosfera dorită.
          </Typography>
        </Container>
      </Box>
    </>
  );
};

export default Mixes;
