import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Button,
  Grid,
  Card,
  CardContent,
  Box,
  Chip,
  Fade
} from '@mui/material';
import GlassIcons from '../components/GlassIcons';
import {
  MusicNote,
  Event,
  People,
  Star,
  FavoriteOutlined,
  CelebrationOutlined,
  ChildCareOutlined,
  BusinessOutlined
} from '@mui/icons-material';
import ReviewsCarousel from '../components/ReviewsCarousel';

const CounterAnimation = ({ target, prefix = '', duration = 2000 }) => {
  const [count, setCount] = useState(0);
  const [isVisible, setIsVisible] = useState(false);
  const elementRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !isVisible) {
          setIsVisible(true);
        }
      },
      { threshold: 0.5 }
    );

    if (elementRef.current) {
      observer.observe(elementRef.current);
    }

    return () => observer.disconnect();
  }, [isVisible]);

  useEffect(() => {
    if (!isVisible) return;

    const startTime = Date.now();
    const endTime = startTime + duration;

    const updateCount = () => {
      const now = Date.now();
      const progress = Math.min((now - startTime) / duration, 1);

      const easeOutCubic = 1 - Math.pow(1 - progress, 3);
      const currentCount = Math.floor(easeOutCubic * target);

      setCount(currentCount);

      if (now < endTime) {
        requestAnimationFrame(updateCount);
      } else {
        setCount(target);
      }
    };

    requestAnimationFrame(updateCount);
  }, [isVisible, target, duration]);

  return (
    <span ref={elementRef}>
      {prefix}{count}
    </span>
  );
};

const Home = () => {
  const navigate = useNavigate();

  return (
    <>
      {/* Hero Section with Banner */}
      <Box
        className="relative flex items-center justify-center overflow-hidden"
        sx={{
          height: '100vh',
          minHeight: '800px',
          backgroundImage: 'url(/images/banner.jpg)',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundAttachment: 'fixed',
          position: 'relative',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            zIndex: 1,
          }
        }}
      >
        <Box
          sx={{
            zIndex: 2,
            position: 'absolute',
            bottom: '20%',
            left: '50%',
            transform: 'translateX(-50%)',
            textAlign: 'center'
          }}
        >
          <Fade in timeout={1000}>
            <Typography
              variant="h3"
              sx={{
                color: 'rgba(255, 255, 255, 0.6)',
                fontFamily: '"Arial", "Helvetica", sans-serif',
                fontWeight: 'bold',
                fontSize: { xs: '2.5rem', md: '3.5rem' },
                mb: 4,
                textShadow: '2px 2px 4px rgba(0,0,0,0.7)',
                fontStyle: 'italic',
                letterSpacing: '2px'
              }}
            >
              „Muzica e singura limbă pe care o înțeleg toți."
            </Typography>
          </Fade>
          <Fade in timeout={1500}>
            <Box sx={{ display: 'flex', justifyContent: 'center' }}>
              <GlassIcons
                items={[
                  {
                    icon: "Contactează-mă",
                    label: "",
                    color: "transparent",
                    onClick: () => navigate('/contact')
                  }
                ]}
                className="contact-glass-button"
              />
            </Box>
          </Fade>
        </Box>
      </Box>

      {/* Statistics Section */}
      <Box className="py-20 bg-gray-50">
        <Container maxWidth="lg">
          <Grid container spacing={6} justifyContent="center">
            {[
              {
                number: 875,
                label: 'CLIENȚI FERICIȚI',
                icon: <FavoriteOutlined sx={{ fontSize: '8rem' }} />
              },
              {
                number: 346,
                label: 'NUNTI',
                icon: <CelebrationOutlined sx={{ fontSize: '8rem' }} />
              },
              {
                number: 209,
                label: 'BOTEZURI',
                icon: <ChildCareOutlined sx={{ fontSize: '8rem' }} />
              },
              {
                number: 125,
                label: 'EVENIMENTE CORPORATE',
                icon: <BusinessOutlined sx={{ fontSize: '8rem' }} />
              }
            ].map((stat, index) => (
              <Grid item xs={12} sm={6} md={3} key={index}>
                <Fade in timeout={1000 + index * 200}>
                  <Box
                    className="text-center"
                    sx={{
                      position: 'relative',
                      py: 4,
                      px: 2,
                      '&:not(:last-child)::after': {
                        content: '""',
                        position: 'absolute',
                        right: { xs: 'auto', md: '-3rem' },
                        bottom: { xs: '-3rem', md: 'auto' },
                        left: { xs: '50%', md: 'auto' },
                        top: { xs: 'auto', md: '50%' },
                        transform: { xs: 'translateX(-50%)', md: 'translateY(-50%)' },
                        width: { xs: '80px', md: '2px' },
                        height: { xs: '2px', md: '80px' },
                        backgroundColor: '#cccccc'
                      }
                    }}
                  >
                    {/* Background Icon */}
                    <Box
                      sx={{
                        position: 'absolute',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                        color: '#000000',
                        opacity: 0.1,
                        zIndex: 1
                      }}
                    >
                      {stat.icon}
                    </Box>
                    <Typography
                      variant="h2"
                      sx={{
                        fontSize: { xs: '2.5rem', md: '3rem' },
                        fontWeight: 'bold',
                        color: '#000000',
                        mb: 1,
                        position: 'relative',
                        zIndex: 2
                      }}
                    >
                      <CounterAnimation
                        target={stat.number}
                        prefix={stat.prefix || ''}
                        duration={6000}
                      />
                    </Typography>
                    <Typography
                      variant="h6"
                      sx={{
                        color: '#666666',
                        fontWeight: 'bold',
                        letterSpacing: '1px',
                        position: 'relative',
                        zIndex: 2
                      }}
                    >
                      {stat.label}
                    </Typography>
                  </Box>
                </Fade>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* Reviews Carousel Section */}
      <ReviewsCarousel />
    </>
  );
};

export default Home;
